import React from 'react';

import StarRatingComponent from 'react-star-rating-component';

const allStars = [
  {
    name: 'start1',
    value: 0,
  },
];
class App extends React.Component {
  constructor() {
    super();

    this.state = {
      rating: 1,
      rating_custom_icon: 5,
      // rating_half_star_initial: 0,
      starObject: allStars,
      rating_empty_initial: 0,
      totalStar: this.rating_empty_initial,
      numberOfStars: allStars.length,
      rating_half_star: this.totalStar / this.numberOfStars,
    };
  }

  onSubmit = () => {
    console.log('rating_half_star', this.state.rating_half_star);
    console.log('rating_half_star', this.state.rating_half_star);
    allStars.length === 1
      ? (allStars[this.state.numberOfStars - 1].value = this.state.rating_empty_initial)
      : allStars.push({
          name: 'star' + (allStars.length + 1),
          value: this.state.rating_empty_initial,
        });
    this.setState({ rating_empty_initial: 0 });
  };

  onStarClick = (nextValue, prevValue, name) => {
    console.log('name: %s, nextValue: %s, prevValue: %s', name, nextValue, prevValue);
    this.setState({ rating: nextValue });
  };

  onStarClickCustomIcon = (nextValue, prevValue, name) => {
    console.log('name: %s, nextValue: %s, prevValue: %s', name, nextValue, prevValue);
    this.setState({ rating_custom_icon: nextValue });
  };

  onStarClickHalfStar = (nextValue, prevValue, name, e) => {
    const xPos =
      (e.pageX - e.currentTarget.getBoundingClientRect().left) / e.currentTarget.offsetWidth;

    if (xPos <= 0.5) {
      nextValue -= 0.5;
    }

    console.log('name: %s, nextValue: %s, prevValue: %s', name, nextValue, prevValue);
    // console.log(e);
    this.setState({ rating_half_star: nextValue });
  };

  onStarClickEmptyInitial = (nextValue, prevValue, name) => {
    console.log('name: %s, nextValue: %s, prevValue: %s', name, nextValue, prevValue);
    this.setState({ rating_empty_initial: nextValue, rating_half_star: nextValue });
  };

  render() {
    return (
      <div style={{ marginLeft: 20 }}>
        <h2>ReactStarRatingComponent</h2>

        {/* <h3>Editable:</h3>
        <div style={{ fontSize: 24 }}>
          <StarRatingComponent name="app1" />
        </div>

        <h3>Editable with handlers (Rating from state is {this.state.rating}):</h3>
        <div style={{ fontSize: 26 }}>
          <StarRatingComponent
            name="app2"
            starCount={8}
            value={this.state.rating}
            onStarClick={this.onStarClick.bind(this)}
          />
        </div>

        <h3>Editable (with custom icons):</h3>
        <div style={{ fontSize: 20 }}>
          <StarRatingComponent
            name="app3"
            starCount={10}
            value={this.state.rating_custom_icon}
            onStarClick={this.onStarClickCustomIcon.bind(this)}
            starColor="#f00"
            renderStarIcon={() => <span></span>}
          />
        </div>

        <h3>Non-Editable:</h3>
        <div style={{ fontSize: 18 }}>
          <StarRatingComponent name="app4" editing={false} starCount={10} value={8} />
        </div> */}

        <h3>Average ({this.state.rating_half_star}):</h3>
        <div style={{ fontSize: 24 }}>
          <StarRatingComponent
            name="app6"
            starColor="#ffb400"
            emptyStarColor="#ffb400"
            value={this.state.rating_half_star}
            // onStarClick={this.onStarClickHalfStar.bind(this)}
            editing={false}
            renderStarIcon={(index, value) => {
              return (
                <span>
                  <i className={index <= value ? 'fas fa-star' : 'far fa-star'} />
                </span>
              );
            }}
            renderStarIconHalf={() => {
              return (
                <span>
                  <span style={{ position: 'absolute' }}>
                    <i className="far fa-star" />
                  </span>
                  <span>
                    <i className="fas fa-star-half" />
                  </span>
                </span>
              );
            }}
          />
        </div>

        <h3>Rate & Review Product:</h3>
        <div style={{ fontSize: 34 }}>
          <StarRatingComponent
            name="app5"
            value={this.state.rating_empty_initial}
            onStarClick={this.onStarClickEmptyInitial.bind(this)}
          />
        </div>
        <button type="submit" className="pure-button-primary" onClick={this.onSubmit}>
          Add
        </button>
        <h3>All Reviews:</h3>
        {allStars.map((star, index) => (
          <div style={{ fontSize: 24 }}>
            {
              <StarRatingComponent
                name={star.name}
                value={star.value}
                // onStarClick={this.onStarClickEmptyInitial.bind(this)}
              />
            }
          </div>
        ))}
      </div>
    );
  }
}

export default App;
